const transpile = require\(''./dist/index.js''\).default;
const code = \\`
class KeyBindingShell
  constructor: ->
    @mapBound = {}
    @mapCallback = {}
    @mapPrevented = {}

  prepare: \(key\) ->
    if @mapCallback[key] then return
    @mapBound[key] = => @fire key
    @mapCallback[key] = []
    @mapPrevented[key] = false
    return

  fire: \(key\) ->
    console.log ''Firing: '' + key
\\`;
transpile\(code, { string: true, verbose: true }\).then\(result => {
  console.log\(''\\n=== Generated AHK ===''\);
  console.log\(result\);
}\).catch\(err => {
  console.error\(''Error:'', err.message\);
}\);
")
